export default function TextForm(props) {
    const [text, setText] = useState('Enter the text here'); 

  return (
    <div>
      <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea value={text}
          className="form-control"
          id="myBox"
          rows="8"
        ></textarea><br></br>
        <button type="button" className="btn btn-outline-danger" onClick={handleUpClick}>{props.button}</button>
      </div>
    </div>
  );
}
