    const handleOnChange = (event) => {
        console.log("On change");
        setText(event.target.value);

    };

    const [text, setText] = useState('Enter the text here'); 

  return (
    <>
    <div className="container">
        <h1>{props.heading}</h1>
      <div className="mb-3">  
        <textarea 
          value={text}
          onChange={handleOnChange}
          className="form-control"
          id="myBox"
          rows="8"
