import React, { useState } from "react";


export default function TextForm(props) {
    const handleUpClick = () =>{
        console.log("Upper case Fired"+ text);
        let newText = text.toUpperCase();
        setText(newText);
    };

    const handleLowerClick = () =>{
        console.log("Upper case Fired"+ text);
        let newText = text.toLowerCase();
        setText(newText);
    };

    const handleOnChange = (event) => {
        console.log("On change");
        setText(event.target.value);

    };

    // const handleFontColor = () => {
    //     console.log("Change color to red "+text);
    //     let font = ;
    //     setText(font);

    // };

    const [text, setText] = useState(''); 

  return (
    <>
    <div className="container">
        <h1>{props.heading}</h1>
      <div className="mb-3">  
        <textarea 
          value={text}
          onChange={handleOnChange}
          className="form-control"
          id="myBox"
          rows="8"
        ></textarea>
        <button type="button" className="btn btn-outline-danger my-1 mx-1" 
         onClick={handleUpClick}>Upper Case
         </button>
         <button type="button" className="btn btn-outline-danger my-1 mx-1" 
         onClick={handleLowerClick}>Lower Case
         </button>
         <button type="button" className="btn btn-outline-danger my-1 mx-1" 
         onClick={handleClearText}>Clear Text
         </button>
      </div>
    </div>

    <div className="container my-3" >
        <h1>Your text Summary</h1>
        <p>{text.split(" ").length} words,
           {text.length} characters
        </p>
        <p>
            Time to read {0.008*text.split(" ").length};
        </p>
        <h3>Preview</h3>
        <p>{text}</p>

    </div>
    </>
  );
}
