import React, {useState} from "react";

export default function TextForm(props) {
  return (
    <div>
      <div className="mb-3">
        <h1>{props.heading}</h1>
        <textarea
          className="form-control"
          id="myBox"
          rows="8"
        ></textarea><br></br>
        <button type="button" className="btn btn-outline-danger">{props.button}</button>
      </div>
    </div>
  );
}
